  )
values (#{aamid,jdbcType=VARCHAR}, #{confid,jdbcType=VARCHAR}, #{confvalue,jdbcType=INTEGER}
  )

mapper.update:
update hopeuserconf


    confvalue = #{confvalue,jdbcType=INTEGER},


